package com.Project3.Backend.Services;import com.Project3.Backend.Models.UserPrincipal;import com.Project3.Backend.Models.User;import com.Project3.Backend.Repositories.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class CustomUserDetailsService implements UserDetailsService {    @Autowired    private UserRepository userRepository;    @Override    public UserDetails loadUserByUsername(String email) throws UsernameNotFoundException {        User user = userRepository.findByEmail(email);        if (user == null) {            System.out.println("User Not Found");            throw new UsernameNotFoundException("user not found");        }        return new UserPrincipal(user);    }}